const box = {
  el: element,
  x: 0, y: 0,
  vx: 0, vy: 0,
  dragging: false
};

element.onmousedown = (e) => {
  box.dragging = true;
  box.offsetX = e.clientX - box.x;
  box.offsetY = e.clientY - box.y;
};

document.onmousemove = (e) => {
  if (!box.dragging) return;
  const nx = e.clientX - box.offsetX;
  const ny = e.clientY - box.offsetY;
  box.vx = (nx - box.x) * 0.3;
  box.vy = (ny - box.y) * 0.3;
  box.x = nx;
  box.y = ny;
};

document.onmouseup = () => {
  box.dragging = false;
  animateBounce(box);
};

function animateBounce(b) {
  function frame() {
    b.vx *= 0.92;
    b.vy *= 0.92;
    b.x += b.vx;
    b.y += b.vy;

    // Bounce off edges
    if (b.x < 0) { b.x = 0; b.vx *= -1; }
    if (b.x > maxX) { b.x = maxX; b.vx *= -1; }
    if (b.y < 0) { b.y = 0; b.vy *= -1; }
    if (b.y > maxY) { b.y = maxY; b.vy *= -1; }

    b.el.style.left = b.x + 'px';
    b.el.style.top = b.y + 'px';

    if (Math.abs(b.vx) > 0.1 || Math.abs(b.vy) > 0.1) {
      requestAnimationFrame(frame);
    }
  }
  frame();
}
