class Connection {
  constructor(canvas) {
    this.x1 = Math.random() * canvas.width;
    this.y1 = Math.random() * canvas.height;
    this.x2 = Math.random() * canvas.width;
    this.y2 = Math.random() * canvas.height;
    this.opacity = Math.random() * 0.5;
    this.pulse = Math.random() * 0.02;
  }

  update() {
    this.opacity += this.pulse;
    if (this.opacity > 0.5 || this.opacity < 0.1) {
      this.pulse *= -1;
    }
  }

  draw(ctx) {
    const gradient = ctx.createLinearGradient(this.x1, this.y1, this.x2, this.y2);
    gradient.addColorStop(0, 'rgba(0, 217, 255, 0)');
    gradient.addColorStop(0.5, `rgba(0, 217, 255, ${this.opacity})`);
    gradient.addColorStop(1, 'rgba(185, 104, 255, 0)');

    ctx.strokeStyle = gradient;
    ctx.globalAlpha = this.opacity;
    ctx.lineWidth = 2;
    ctx.beginPath();
    ctx.moveTo(this.x1, this.y1);
    ctx.lineTo(this.x2, this.y2);
    ctx.stroke();
    ctx.shadowBlur = 10;
    ctx.shadowColor = '#00d9ff';
  }
}
