  "name": "Enrich Usage Data",
  "type": "n8n-nodes-base.code",
  "parameters": {
    "jsCode": "const items = $input.all();\nconst config = items[0].json;\n\nreturn items.map(item => {\n  const body = item.json.body || {};\n  \n  // Extract task type from query or classify it\n  const query = body.query || '';\n  const taskType = body.taskType || classifyTaskType(query);\n  \n  return {\n    json: {\n      usage_id: $execution.id + '_' + Date.now(),\n      timestamp: new Date().toISOString(),\n      model_name: body.modelName,\n      task_type: taskType,\n      query: query,\n      query_length: query.length,\n      query_complexity: calculateComplexity(query),\n      execution_time_ms: body.executionTime || null,\n      success: body.success !== false,\n      user_satisfied: null, // Will be updated via feedback\n      rerun_count: 0,\n      error_message: body.error || null,\n      metadata: JSON.stringify(body.metadata || {})\n    }\n  };\n});\n\nfunction classifyTaskType(query) {\n  const lower = query.toLowerCase();\n  if (lower.includes('code') || lower.includes('function') || lower.includes('debug')) return 'coding';\n  if (lower.includes('explain') || lower.includes('what is') || lower.includes('how does')) return 'explanation';\n  if (lower.includes('write') || lower.includes('create') || lower.includes('generate')) return 'generation';\n  if (lower.includes('analyze') || lower.includes('review') || lower.includes('evaluate')) return 'analysis';\n  if (lower.includes('translate') || lower.includes('convert')) return 'translation';\n  return 'general';\n}\n\nfunction calculateComplexity(query) {\n  const wordCount = query.split(/\\s+/).length;\n  const hasCode = /```|`/.test(query);\n  const hasMultipleQuestions = (query.match(/\\?/g) || []).length > 1;\n  \n  if (wordCount > 200 || hasCode || hasMultipleQuestions) return 'high';\n  if (wordCount > 50) return 'medium';\n  return 'low';\n}"
  }
}

