{
  "name": "Scan MCP Config Directory",
  "type": "n8n-nodes-base.code",
  "parameters": {
    "jsCode": "// Scan MCP Config Directory for server definition files\nconst fs = require('fs');\nconst path = require('path');\n\n// Define the MCP config directory path\n// Adjust this path based on your system configuration\nconst mcpConfigDir = process.env.MCP_CONFIG_DIR || path.join(process.env.HOME || process.env.USERPROFILE, '.config', 'mcp');\n\nconst serverConfigs = [];\n\ntry {\n  // Check if directory exists\n  if (!fs.existsSync(mcpConfigDir)) {\n    console.log(`MCP config directory not found: ${mcpConfigDir}`);\n    return [{ json: { servers: [], count: 0, directory: mcpConfigDir, error: 'Directory not found' } }];\n  }\n\n  // Read all files in the directory\n  const files = fs.readdirSync(mcpConfigDir);\n  \n  // Filter for JSON configuration files\n  const configFiles = files.filter(file => file.endsWith('.json') || file === 'mcp.json');\n  \n  console.log(`Found ${configFiles.length} config files in ${mcpConfigDir}`);\n  \n  // Read and parse each configuration file\n  for (const file of configFiles) {\n    const filePath = path.join(mcpConfigDir, file);\n    \n    try {\n      const fileContent = fs.readFileSync(filePath, 'utf8');\n      const config = JSON.parse(fileContent);\n      \n      // Extract server definitions\n      if (config.mcpServers && typeof config.mcpServers === 'object') {\n        // Handle mcpServers object format\n        for (const [serverName, serverDef] of Object.entries(config.mcpServers)) {\n          serverConfigs.push({\n            name: serverName,\n            command: serverDef.command,\n            args: serverDef.args || [],\n            env: serverDef.env || {},\n            capabilities: serverDef.capabilities || [],\n            metadata: {\n              sourceFile: file,\n              filePath: filePath,\n              discoveredAt: new Date().toISOString()\n            }\n          });\n        }\n      } else if (config.servers && Array.isArray(config.servers)) {\n        // Handle servers array format\n        for (const server of config.servers) {\n          serverConfigs.push({\n            name: server.name,\n            command: server.command,\n            args: server.args || [],\n            env: server.env || {},\n            capabilities: server.capabilities || [],\n            metadata: {\n              sourceFile: file,\n              filePath: filePath,\n              discoveredAt: new Date().toISOString()\n            }\n          });\n        }\n      }\n    } catch (parseError) {\n      console.error(`Error parsing ${file}:`, parseError.message);\n    }\n  }\n  \n  console.log(`Successfully parsed ${serverConfigs.length} MCP server configurations`);\n  \n  return [{\n    json: {\n      servers: serverConfigs,\n      count: serverConfigs.length,\n      directory: mcpConfigDir,\n      scannedAt: new Date().toISOString()\n    }\n  }];\n  \n} catch (error) {\n  console.error('Error scanning MCP config directory:', error.message);\n  return [{\n    json: {\n      servers: [],\n      count: 0,\n      directory: mcpConfigDir,\n      error: error.message\n    }\n  }];\n}"
  }
}

