{
  "name": "Parse Claude Request",
  "type": "n8n-nodes-base.code",
  "parameters": {
    "jsCode": "// H칛mta data fr친n webhook\nconst body = $input.item.json.body || $input.item.json;\nconst action = body.action;\nconst taskData = body.task || {};\nconst gitData = body.git || {};\nconst calendarData = body.calendar || {};\n\n// Validera action\nconst validActions = [\n  'create', 'update', 'get', 'delete', 'get_many',\n  'git.create_issue', 'git.list_files', 'git.get_file', 'git.create_file', 'git.get_issues',\n  'calendar.create', 'calendar.update', 'calendar.get', 'calendar.delete', 'calendar.get_many'\n];\n\nif (!validActions.includes(action)) {\n  throw new Error(`Invalid action: \"${action}\". Valid: ${validActions.join(', ')}`);\n}\n\n// F칬r ClickUp actions\nif (['create', 'update', 'get', 'delete', 'get_many'].includes(action)) {\n  return {\n    json: {\n      action: action,\n      taskName: taskData.name || '',\n      taskId: taskData.id || null,\n      status: taskData.status || null,\n      priority: taskData.priority || 3,\n      description: taskData.description || '',\n      timestamp: new Date().toISOString()\n    }\n  };\n}\n\n// F칬r Git actions\nif (action.startsWith('git.')) {\n  return {\n    json: {\n      action: action,\n      repo: gitData.repo || 'workspace',\n      owner: gitData.owner || 'itsimonfredlingjack',\n      filePath: gitData.path || '',\n      fileContent: gitData.content || '',\n      issueTitle: gitData.title || '',\n      issueBody: gitData.body || '',\n      commitMessage: gitData.commit || 'Update from Claude',\n      timestamp: new Date().toISOString()\n    }\n  };\n}\n\n// F칬r Calendar actions\nif (action.startsWith('calendar.')) {\n  return {\n    json: {\n      action: action,\n      eventId: calendarData.id || null,\n      summary: calendarData.title || '',\n      description: calendarData.description || '',\n      startTime: calendarData.start || '',\n      endTime: calendarData.end || '',\n      timestamp: new Date().toISOString()\n    }\n  };\n}"
  }
}

